import { useState, SyntheticEvent } from "react";

import {  TextField, Grid, Button  } from '@mui/material';

import { EntryFormValues, EntryType } from "../../../types";

interface Props {
  onCancel: () => void;
  onSubmit: (values: EntryFormValues) => void;
  type: EntryType
}

const AddEntryForm = ({ onCancel, onSubmit, type }: Props) => {
  const [description, setDescription] = useState('');
  const [date, setDate] = useState('');
  const [specialist, setSpecialist] = useState('');
  const [diagnosisCodes, setCodes] = useState<string[]>([])

  const addEntry = (event: SyntheticEvent) => {
    event.preventDefault();

    switch (type) {
      case EntryType.Hospital:
        return onSubmit({
          description,
          date,
          specialist,
          diagnosisCodes,
          discharge,
          type: "Hospital"
        });
      case EntryType.HealthCheck:
        return onSubmit({
          description,
          date,
          specialist,
          diagnosisCodes,
          healthCheckRating,
          type: "HealthCheck"
        });
      case EntryType.OccupationalHealthcare:
        return onSubmit({
          description,
          date,
          specialist,
          diagnosisCodes,
          employerName,
          sickLeave,
          type: "OccupationalHealthCare"
        });
      default:
        return;
    }
  };

  const createCodesArray = (value: string | undefined) => {
    if (value) {
      const codeArray = value.split(" ");
      return setCodes(codeArray);
    }

    return setCodes([]);
  };

  switch (type) {
    case EntryType.Hospital:
      return (
        <div>
          <form onSubmit={addEntry}>
            <TextField
              label="Date"
              fullWidth 
              value={date}
              onChange={({ target }) => setDate(target.value)}
            />
            <TextField
              label="Description"
              fullWidth
              value={description}
              onChange={({ target }) => setDescription(target.value)}
            />
            <TextField
              label="Specialist"
              fullWidth
              value={specialist}
              onChange={({ target }) => setSpecialist(target.value)}
            />
            <TextField
              label="Diagnosis Codes"
              fullWidth
              value={diagnosisCodes}
              onChange={({ target }) => createCodesArray(target.value)}
            />
    
            <Grid>
              <Grid item>
                <Button
                  color="secondary"
                  variant="contained"
                  style={{ float: "left" }}
                  type="button"
                  onClick={onCancel}
                >
                  Cancel
                </Button>
              </Grid>
              <Grid item>
                <Button
                  style={{
                    float: "right",
                  }}
                  type="submit"
                  variant="contained"
                >
                  Add
                </Button>
              </Grid>
            </Grid>
          </form>
        </div>
      );
    case EntryType.HealthCheck:
      return(
        <div>

        </div>
      );
    case EntryType.OccupationalHealthcare:
      return(
        <div>

        </div>
      );
    default:
      return(
        <div>

        </div>
      );
  }
};

export default AddEntryForm;
